import React, { Component } from "react";
import './soundpad.css';


class SoundPad extends Component {
    render() {
        return (

                <button className="sound-pad" onClick='play()'>
                    {this.props.value}
                </button>
        )

    }

    componentDidMount() {
        this.audio = new Audio('./')
        this.audio.load()
        this.playAudio()
  }

  play() {
      const audioPromise = this.audio.play()
      if (audioPromise !== undefined) {
        audioPromise
          .then(_ => {
            // autoplay started
          })
          .catch(err => {
            // catch dom exception
            console.info(err)
          })
      }
  }

}

export default SoundPad;
