import React, { Component } from "react";
import './soundpad.css';
import useSound from 'use-sound';
import sound1 from './sounds/test1.mp3';

const PlaySound = () => {
    const [play] = useSound(sound1);

    return play;
  };

class SoundPad extends Component {
    render() { 
        const PlaySound = () => {
        const [play] = useSound(sound1);
        return (

                <button className="sound-pad" onClick={play}>
                    {this.props.value}


                </button>
        )

    }
};
}






export default SoundPad;
