import React, { Component } from "react";
import './soundpad.css';


class SoundPad extends Component {
    constructor(props) {
        super(props);
        this.state = {

          play: false,
          pause: true

        };

        this.url = "./sounds/";
        this.audio = new Audio(this.url);

      }

      play(){
        this.setState({
          play: true,
          pause: false
        });
        console.log(this.audio);
        this.audio.play();
      }

      pause(){
      this.setState({ play: false, pause: true });
        this.audio.pause();
      }
    render() {
        return (

                <button className="sound-pad" onClick={this.play}>
                    {this.props.value}
                </button>
        )

    }


}






export default SoundPad;
