import React, { Component } from "react";
import './soundpad.css';
import useSound from 'use-sound';
import sound1 from './sounds/test1.mp3';

const BoopButton = () => {
    const [play] = useSound(sound1);

    return play;
  };

class SoundPad extends Component {
    render() {
        return (

                <button className="sound-pad" onClick={BoopButton()}>
                    {this.props.value}


                </button>
        )

    }
}






export default SoundPad;
