import React, { Component } from "react";
import './soundpad.css';
import useSound from 'use-sound';
import sound1 from './sounds/test1.mp3';

const PlaySound = () => {
    const pad1 = document.getElementsByClassName('sound-pad')[0];
    const [play] = useSound(sound1);
    pad1.addEventListener('click', play);
}

class SoundPad extends Component{
    render(){
        return(
            <button className="sound-pad">
                    {this.props.value}
            </button>
        )
    }



